mapper.update:

UPDATE users
SET password = #{password}
WHERE email = #{email}

UPDATE CUSTOMERINQUIRIES
    SET title = #{title},
        content = #{content}
    WHERE email = #{email}

mapper.select:
SELECT COUNT(*) FROM wishlist 
WHERE user_id = #{userId} 
AND movie_id = #{movieId}

mapper.delete:
DELETE FROM wishlist 
WHERE user_id = #{userId} 
  AND movie_id = #{movieId}

